import React from "react";
import { connect } from "react-redux";
import ReactDOM from "react-dom";
import { Provider } from "react-redux";
import { BrowserRouter, Route, Switch } from "react-router-dom";
import { ScrollContext } from "react-router-scroll-4";
import { IntlReducer as Intl, IntlProvider } from "react-redux-multilingual";
import "./index.scss";

// Import custom components
import store from "./store";
import * as actions from "./actions/auth";
import translations from "./constants/translations";
import { getAllProducts } from "./actions";
import Landing from "./components/landing";

// Layouts
import Fashion from "./components/layouts/fashion/main";
import Vegetables from "./components/layouts/vegetables/main";
import Kids from "./components/layouts/kids/main";
import Pets from "./components/layouts/pets/main";
import Furniture from "./components/layouts/furniture/main";
import Watch from "./components/layouts/watch/main";
import Beauty from "./components/layouts/beauty/main";
import Electronic from "./components/layouts/electronic/main";

//Collection Pages
import CollectionLeftSidebar from "./components/collection/collection-left-sidebar";
import CollectionNoSidebar from "./components/collection/collection-no-sidebar";
import CollectionRightSidebar from "./components/collection/collection-right-sidebar";
import CollectionFullWidth from "./components/collection/collection-full-width";
import CollectionMetro from "./components/collection/collection-metro";

// Product Pages
import LeftSideBar from "./components/products/left-sidebar";
import RightSideBar from "./components/products/right-sidebar";
import NoSideBar from "./components/products/no-sidebar";
import LeftImage from "./components/products/left-image";
import RightImage from "./components/products/right-image";
import Accordian from "./components/products/accordian";
import ColumnLeft from "./components/products/column-left";
import ColumnRight from "./components/products/column-right";
import Column from "./components/products/column";
import Vertical from "./components/products/vertical";

// Features
import Layout from "./components/app";
import Cart from "./components/cart";
import Compare from "./components/compare/index";
// import wishList from "./components/wishlist";
// import checkOut from "./components/checkout";
// import orderSuccess from "./components/checkout/success-page";

// Extra Pages
import aboutUs from "./components/pages/about-us";
import PageNotFound from "./components/pages/404";
import lookbook from "./components/pages/lookbook";
// import Login from "./components/pages/login";
// import Register from "./components/pages/register";
import Search from "./components/pages/search";
import Collection from "./components/pages/collection";
// import ForgetPassword from "./components/pages/forget-password";
import Contact from "./components/pages/contact";
// import Dashboard from "./components/pages/dashboard";
import Faq from "./components/pages/faq";

// Blog Pages
import RightSide from "./components/blogs/right-sidebar";
import Details from "./components/blogs/details";
import BlogPage from "./components/blogs/blog-page";

// Theme Element
import ElementTitle from "./components/features/theme/element-title";
import ElementBanner from "./components/features/theme/element-banner";
import ElementSlider from "./components/features/theme/element-slider";
import ElementCategory from "./components/features/theme/element-category";
import ElementService from "./components/features/theme/element-service";
import ElementRatio from "./components/features/theme/element-ratio";

// Product Elements
import ElementProductBox from "./components/features/product/element-product-box";
import ElementProductSlider from "./components/features/product/element-product-slider";
import ElementProductNoSlider from "./components/features/product/element-product-no-slider";
import ElementMultipleSlider from "./components/features/product/element-multiple-slider";
import ElementProductTab from "./components/features/product/element-product-tab";

// Portfolio Features
import GridCols from "./components/features/portfolio/grid-cols";
import MasonaryGridCols from "./components/features/portfolio/masonary-grid-cols";

//local dukans
import homepage from "./components/ld/homepage/main";
import LocalityHome from "./components/ld/localityHome/main";
import LocalityList from "./components/ld/localityList/main";
import ShopHome from "./components/ld/shopHome/main";
import ShopList from "./components/ld/shopHome/list";
import Login from "./components/ld/pages/login";
import Register from "./components/ld/pages/register";
import PassworReset from "./components/ld/pages/forgetPassword";

import Orders from "./components/ld/orders/main";
import ManageOrderFilter from "./components/ld/manageOrders/index";
import ManageOrderSearch from "./components/ld/manageOrders/searchOrder";
import ShopOrderManage from "./components/ld/shopManagement/test";
import OrderTable from "./components/ld/manageOrders/tableIndex";
import ShopOrderTable from "./components/ld/manageOrders/shopOrderTable";

import OrderItem from "./components/ld/orders/order-item";
import Address from "./components/ld/address/main";
import CreateAddress from "./components/ld/address/add2";
import EditAddress from "./components/ld/address/edit";
import Wishlist from "./components/ld/wishlist/main";
import Checkout from "./components/ld/checkout/main";
import OrderSummary from "./components/ld/checkout/orderSummary";

//products
import ShopProducts from "./components/ld/products/productList";
import ShopProductsEdit from "./components/ld/products/editProduct2";
// import AddProduct from "./components/ld/products/addProduct"
// import EditProduct from "./components/ld/products/addProduct"
// import DeleteProduct from "./components/ld/products/addProduct"

import AddShop from "./components/ld/manageShops/addShop";
import ManageShopFilter from "./components/ld/manageShops/filterIndex";
import ManageShopSearch from "./components/ld/manageShops/searchShop";
import AddCandidate from "./components/ld/career/register";
import AddComplaint from "./components/ld/complaint/register";

class Root extends React.Component {
  // componentDidMount() {
  //   this.props.onTryAutoSignup();
  //   console.log("hi");
  // }
  render() {
    // store.dispatch(getAllProducts());

    return (
      <Provider store={store}>
        <IntlProvider translations={translations} locale="en">
          <BrowserRouter basename={"/"}>
            <ScrollContext>
              <Switch>
                <Route
                  path={`${process.env.PUBLIC_URL}/login`}
                  component={Login}
                />
                <Route
                  path={`${process.env.PUBLIC_URL}/register`}
                  component={Register}
                />
                <Route
                  path={`${process.env.PUBLIC_URL}/reset-password`}
                  component={PassworReset}
                />
                <Layout>
                  <Route
                    exact
                    path={`${process.env.PUBLIC_URL}/`}
                    component={homepage}
                  />
                  <Route
                    path={`${process.env.PUBLIC_URL}/places/:placeID`}
                    component={LocalityHome}
                  />
                  <Route
                    path={`${process.env.PUBLIC_URL}/place-list`}
                    component={LocalityList}
                  />
                  <Route
                    path={`${process.env.PUBLIC_URL}/shops/:shopID`}
                    component={ShopHome}
                  />
                  <Route
                    path={`${process.env.PUBLIC_URL}/editaddress/:addressID`}
                    component={EditAddress}
                  />
                  <Route
                    path={`${process.env.PUBLIC_URL}/create-address`}
                    component={CreateAddress}
                  />
                  <Route
                    path={`${process.env.PUBLIC_URL}/addresses`}
                    component={Address}
                  />
                  <Route
                    path={`${process.env.PUBLIC_URL}/shop-product-list`}
                    component={ShopProducts}
                  />
                  <Route
                    path={`${process.env.PUBLIC_URL}/edit-shop-product/:productID`}
                    component={ShopProductsEdit}
                  />
                  {/* <Route
                    path={`${process.env.PUBLIC_URL}/edit-product`}
                    component={Orders}
                  /> */}
                  <Route
                    path={`${process.env.PUBLIC_URL}/order/:orderID`}
                    component={OrderItem}
                  />
                  <Route
                    path={`${process.env.PUBLIC_URL}/orders`}
                    component={Orders}
                  />
                  <Route
                    path={`${process.env.PUBLIC_URL}/manage-order-delivery`}
                    component={OrderTable}
                  />
                  <Route
                    path={`${process.env.PUBLIC_URL}/wishlist`}
                    component={Wishlist}
                  />
                  <Route
                    path={`${process.env.PUBLIC_URL}/checkout`}
                    component={Checkout}
                  />
                  <Route
                    path={`${process.env.PUBLIC_URL}/ordersummary`}
                    component={OrderSummary}
                  />
                  <Route
                    path={`${process.env.PUBLIC_URL}/manage-order-filter`}
                    component={ManageOrderFilter}
                  />
                  <Route
                    path={`${process.env.PUBLIC_URL}/manage-order-search`}
                    component={ManageOrderSearch}
                  />
                  <Route
                    path={`${process.env.PUBLIC_URL}/shop-order-manage`}
                    component={ShopOrderManage}
                  />
                  <Route
                    path={`${process.env.PUBLIC_URL}/add-shop`}
                    component={AddShop}
                  />
                  <Route
                    path={`${process.env.PUBLIC_URL}/shop-filter`}
                    component={ManageShopFilter}
                  />
                  <Route
                    path={`${process.env.PUBLIC_URL}/shop-order-table`}
                    component={ShopOrderTable}
                  />
                  <Route
                    path={`${process.env.PUBLIC_URL}/shop-search`}
                    component={ManageShopSearch}
                  />
                  <Route
                    path={`${process.env.PUBLIC_URL}/add-candidate`}
                    component={AddCandidate}
                  />
                  <Route
                    path={`${process.env.PUBLIC_URL}/add-complaint`}
                    component={AddComplaint}
                  />
                </Layout>
              </Switch>
            </ScrollContext>
          </BrowserRouter>
        </IntlProvider>
      </Provider>
    );
  }
}

ReactDOM.render(<Root />, document.getElementById("root"));
