import React, { Component } from "react";
import { Helmet } from "react-helmet";
import { connect } from "react-redux";
import "../../common/index.scss";
import Slider from "react-slick";
import Jumbotron from "react-bootstrap/Jumbotron";
// import { Link } from "react-router-dom";
import axios from "axios";
import { fetchCart } from "../../../actions/cart";

// import { Slider3 } from "../../../services/script";
// import Category from "./category";
import Shipping from "./shipping";
import Trending from "./trending";
import ProductList from "./product-list";
// import Search from "../../pages/search";
// import Header from "../common/header";

import {
  ShopProductListURL,
  ShopFProductListURL,
  ShopDetailURL
} from "../../../constants";

class Shop extends Component {
  state = {
    loading: false,
    error: null,
    products: [],
    featuredProducts: [],
    ShopDetail: null
  };

  componentDidMount() {
    document.getElementById("color").setAttribute("href", `#`);
    this.fetchProducts();
    this.fetchfeaturedProducts();
    this.fetchShopDetails();
    this.props.refreshCart();
  }

  fetchProducts = () => {
    const {
      match: { params }
    } = this.props;
    this.setState({ loading: true });
    axios
      .get(ShopProductListURL(params.shopID))
      .then(res => {
        this.setState({ products: res.data, loading: false });
      })
      .catch(err => {
        this.setState({ error: err, loading: false });
      });
  };

  fetchShopDetails = () => {
    const {
      match: { params }
    } = this.props;
    this.setState({ loading: true });
    axios
      .get(ShopDetailURL(params.shopID))
      .then(res => {
        this.setState({ ShopDetail: res.data, loading: false });
      })
      .catch(err => {
        this.setState({ error: err, loading: false });
      });
  };

  fetchfeaturedProducts = () => {
    const {
      match: { params }
    } = this.props;
    this.setState({ loading: true });
    axios
      .get(ShopFProductListURL(params.shopID))
      .then(res => {
        this.setState({ featuredProducts: res.data, loading: false });
      })
      .catch(err => {
        this.setState({ error: err, loading: false });
      });
  };

  render() {
    const { products, featuredProducts, ShopDetail } = this.state;
    // console.log(ShopDetail);

    return (
      <div>
        <Helmet>
          <title>Local Dukans</title>
        </Helmet>
        {/* <Header /> */}

        <section className="p-0">
          <Slider className="slide-1 home-slider">
            <div>
              <div className="home home39 text-center">
                <div className="container">
                  <div className="row">
                    <div className="col">
                      <div className="slider-contain">
                        <div>
                          {ShopDetail ? <h1>{ShopDetail.name}</h1> : null}
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <div>
              <div className="home home38 text-center">
                <div className="container">
                  <div className="row">
                    <div className="col">
                      <div className="slider-contain">
                        <div>
                          <div>
                            {ShopDetail ? <h1>{ShopDetail.name}</h1> : null}
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </Slider>
        </section>

        {ShopDetail && (
          <div>
            {ShopDetail.is_active ? (
              <div>
                {ShopDetail.is_accepting_orders ? (
                  <div>
                    {/* <Shipping /> */}
                    {ShopDetail && (
                      <div>
                        {ShopDetail.shipping_message !== "" ? (
                          <Shipping message={ShopDetail.shipping_message} />
                        ) : null}
                      </div>
                    )}

                    {featuredProducts && (
                      <Trending fProducts={featuredProducts} />
                    )}

                    {/* <Category /> */}
                    {/* <Search /> */}

                    {products && <ProductList products={products} />}
                  </div>
                ) : (
                  <Jumbotron>
                    <h4>We are not accepting orders now</h4>
                    <p>please check back later</p>
                  </Jumbotron>
                )}
              </div>
            ) : (
              <Jumbotron>
                <h4>This shop is currently not active</h4>
                <p>please check back later</p>
              </Jumbotron>
            )}
          </div>
        )}
      </div>
    );
  }
}

const mapDispatchToProps = dispatch => {
  return {
    refreshCart: () => dispatch(fetchCart())
  };
};
export default connect(
  null,
  mapDispatchToProps
)(Shop);
